INSERT INTO random_events (title, description, category, is_common, health_min, health_max, option_a_text, option_a_effects, option_a_outcome_text, option_b_text, option_b_effects, option_b_outcome_text, is_active) VALUES
('Boxer Entrance Song Request', 'A professional boxer wants to use your song as their ring entrance music for an upcoming title fight. The exposure would be massive!', 'industry', false, NULL, NULL, 'License the song', '{"cash": {"min": 2000, "max": 5000}, "fame": {"min": 100, "max": 300}, "fans": {"min": 500, "max": 1500}}'::jsonb, 'The boxer walks out to your song and wins the fight! Your track trends across boxing fan communities.', 'Politely decline', '{"xp": 50}'::jsonb, 'You pass on the opportunity. Maybe next time.', true),
('Wrestler Theme Song Offer', 'A major wrestling promotion wants your song as a wrestler''s new entrance theme. Weekly TV exposure guaranteed!', 'industry', false, NULL, NULL, 'Sign the deal', '{"cash": {"min": 3000, "max": 8000}, "fame": {"min": 150, "max": 400}, "fans": {"min": 800, "max": 2000}}'::jsonb, 'The crowd goes wild every time your song hits! The wrestler becomes a fan favorite and credits your music.', 'Pass on this', '{"xp": 75}'::jsonb, 'You decide wrestling isn''t the right fit for your image.', true),
('Darts Player Walk-On Song', 'A darts champion wants your track as their walk-on music. It''s a quirky opportunity with dedicated fans!', 'industry', false, NULL, NULL, 'Accept the gig', '{"cash": {"min": 500, "max": 1500}, "fame": {"min": 50, "max": 150}, "fans": {"min": 200, "max": 600}}'::jsonb, 'The pub crowds love it! Your song becomes a drinking anthem at darts events worldwide.', 'Not our scene', '{"xp": 25}'::jsonb, 'Darts just isn''t your thing. Fair enough.', true),
('MMA Fighter Entrance Deal', 'An up-and-coming MMA fighter wants your song for their UFC walkout. The fight will be Pay-Per-View!', 'industry', false, NULL, NULL, 'Let''s do it', '{"cash": {"min": 1500, "max": 4000}, "fame": {"min": 80, "max": 250}, "fans": {"min": 400, "max": 1200}}'::jsonb, 'The fighter wins by knockout and celebrates to your music! The clip goes viral.', 'Not interested', '{"xp": 50}'::jsonb, 'You decide to pass on the MMA opportunity.', true),
('Esports Team Theme Request', 'A top esports organization wants your song as their official team anthem. Millions of online viewers!', 'industry', false, NULL, NULL, 'Partner with them', '{"cash": {"min": 5000, "max": 15000}, "fame": {"min": 200, "max": 500}, "fans": {"min": 1000, "max": 3000}}'::jsonb, 'The team wins a major tournament! Your song plays during their victory celebration to millions of viewers.', 'Not our audience', '{"xp": 100}'::jsonb, 'Gaming isn''t your target market. You move on.', true),
('NASCAR Driver Intro Song', 'A NASCAR driver wants your song playing during their pre-race introductions. American motorsport exposure!', 'industry', false, NULL, NULL, 'Rev it up', '{"cash": {"min": 4000, "max": 10000}, "fame": {"min": 120, "max": 350}, "fans": {"min": 600, "max": 1800}}'::jsonb, 'Race day arrives and your song blares across the speedway! The driver finishes on the podium.', 'Wrong fit', '{"xp": 75}'::jsonb, 'Motorsport doesn''t align with your brand right now.', true),
('Olympic Athlete Training Montage', 'An Olympic athlete''s documentary team wants your song for their training montage. Global viewership potential!', 'industry', false, NULL, NULL, 'Inspire greatness', '{"cash": {"min": 1000, "max": 3000}, "fame": {"min": 100, "max": 300}, "fans": {"min": 500, "max": 1500}}'::jsonb, 'The documentary airs worldwide! Your song soundtracks an emotional medal-winning journey.', 'Skip this one', '{"xp": 50}'::jsonb, 'You let this Olympic opportunity pass.', true),
('Football Club Anthem Request', 'A major football club wants to license your song as their new stadium anthem. Sung by thousands of fans!', 'industry', false, NULL, NULL, 'Become a chant', '{"cash": {"min": 8000, "max": 20000}, "fame": {"min": 300, "max": 600}, "fans": {"min": 2000, "max": 5000}}'::jsonb, 'Matchday arrives and 50,000 fans sing your song in unison! The video goes viral globally.', 'Too commercial', '{"xp": 100}'::jsonb, 'You decide against commercializing your music this way.', true);