('9a2e587a-94f7-49ec-8c99-21f95cac5860', 'The Vault', 'Hidden beneath a former bank in Digbeth, this underground space throbs with bass-heavy beats and graffiti-covered walls.', 1, 180, 8, '[{"label":"Dance Floor Frenzy","description":"Lose yourself in the industrial beats","energy_cost":8},{"label":"Graffiti Wall Chat","description":"Meet local artists and DJs near the murals","energy_cost":5}]', '[{"name":"Vault Reserve","price":6,"effect":"+3 energy"},{"name":"Brummie Bitter","price":4}]', '[{"id":"vault_dj_kez","name":"DJ Kez","role":"Resident DJ","personality":"Raw, uncompromising, bass obsessed","dialogueHooks":["dubstep origins","Birmingham scene","underground culture"]},{"id":"vault_owner_rico","name":"Rico Vance","role":"Club Owner","personality":"Street-smart, loyal to regulars","dialogueHooks":["venue history","Digbeth culture","local talent"]}]', '{"fameRequirement":80,"payout":180,"setLengthMinutes":45,"schedule":"11pm-3am","perks":["+3% local fan buzz"]}', '{"live_interactions":true}'),
