
-- =============================================
-- NIGHTCLUB QUESTS & PROGRESS TABLES
-- =============================================

CREATE TABLE public.nightclub_quests (
  id UUID NOT NULL DEFAULT gen_random_uuid() PRIMARY KEY,
  club_id UUID NOT NULL REFERENCES public.city_night_clubs(id) ON DELETE CASCADE,
  npc_id TEXT NOT NULL,
  title TEXT NOT NULL,
  description TEXT NOT NULL,
  quest_type TEXT NOT NULL DEFAULT 'one_time' CHECK (quest_type IN ('one_time', 'repeatable', 'chain')),
  chain_position INTEGER,
  chain_id TEXT,
  requirements JSONB DEFAULT '{}'::jsonb,
  dialogue JSONB NOT NULL DEFAULT '[]'::jsonb,
  rewards JSONB NOT NULL DEFAULT '{}'::jsonb,
  energy_cost INTEGER NOT NULL DEFAULT 10,
  cooldown_hours INTEGER DEFAULT 0,
  is_active BOOLEAN NOT NULL DEFAULT true,
  created_at TIMESTAMPTZ NOT NULL DEFAULT now(),
  updated_at TIMESTAMPTZ NOT NULL DEFAULT now()
);

CREATE TABLE public.player_nightclub_quest_progress (
  id UUID NOT NULL DEFAULT gen_random_uuid() PRIMARY KEY,
  profile_id UUID NOT NULL REFERENCES public.profiles(id) ON DELETE CASCADE,
  quest_id UUID NOT NULL REFERENCES public.nightclub_quests(id) ON DELETE CASCADE,
  status TEXT NOT NULL DEFAULT 'available' CHECK (status IN ('available', 'in_progress', 'completed', 'failed')),
  started_at TIMESTAMPTZ,
  completed_at TIMESTAMPTZ,
  dialogue_state JSONB DEFAULT '{}'::jsonb,
  rewards_claimed BOOLEAN NOT NULL DEFAULT false,
  created_at TIMESTAMPTZ NOT NULL DEFAULT now(),
  UNIQUE(profile_id, quest_id)
);

ALTER TABLE public.nightclub_quests ENABLE ROW LEVEL SECURITY;
ALTER TABLE public.player_nightclub_quest_progress ENABLE ROW LEVEL SECURITY;

CREATE POLICY "Anyone can view nightclub quests" ON public.nightclub_quests FOR SELECT USING (true);
CREATE POLICY "Admins can manage nightclub quests" ON public.nightclub_quests FOR ALL USING (
  EXISTS (SELECT 1 FROM user_roles WHERE user_id = auth.uid() AND role = 'admin')
);

CREATE POLICY "Users can view own quest progress" ON public.player_nightclub_quest_progress FOR SELECT USING (
  profile_id IN (SELECT id FROM public.profiles WHERE user_id = auth.uid())
);
CREATE POLICY "Users can insert own quest progress" ON public.player_nightclub_quest_progress FOR INSERT WITH CHECK (
  profile_id IN (SELECT id FROM public.profiles WHERE user_id = auth.uid())
);
CREATE POLICY "Users can update own quest progress" ON public.player_nightclub_quest_progress FOR UPDATE USING (
  profile_id IN (SELECT id FROM public.profiles WHERE user_id = auth.uid())
);

CREATE INDEX idx_nightclub_quests_club ON public.nightclub_quests(club_id);
CREATE INDEX idx_nightclub_quests_chain ON public.nightclub_quests(chain_id) WHERE chain_id IS NOT NULL;
CREATE INDEX idx_player_quest_progress_profile ON public.player_nightclub_quest_progress(profile_id);
CREATE INDEX idx_player_quest_progress_quest ON public.player_nightclub_quest_progress(quest_id);

CREATE TRIGGER update_nightclub_quests_updated_at
  BEFORE UPDATE ON public.nightclub_quests
  FOR EACH ROW EXECUTE FUNCTION public.update_updated_at_column();

