INSERT INTO public.random_events (title, description, category, is_common, is_active, health_min, health_max, option_a_text, option_a_effects, option_a_outcome_text, option_b_text, option_b_effects, option_b_outcome_text) VALUES
('I Could Really Use a Drink', 'The urge for alcohol hits you hard. Your hands are shaking slightly.', 'addiction_craving', true, true, null, null, 'Head to the bar', '{"energy": 10, "cash": -30, "addiction_severity": 10, "addiction_type": "alcohol"}', 'The buzz feels good but you know this is bad for you.', 'Stay strong', '{"health": 5, "xp": 15}', 'You resisted the urge. Small victory.'),
('A Cold Beer Would Take the Edge Off', 'After a long day, all you can think about is a cold one.', 'addiction_craving', true, true, null, null, 'Grab a beer', '{"energy": 8, "cash": -20, "addiction_severity": 8, "addiction_type": "alcohol"}', 'Just one turned into three. Classic.', 'Have some water instead', '{"health": 5, "xp": 10}', 'You chose water. Your body thanks you.'),
('The Bar Is Calling My Name', 'You walk past your favourite bar and the pull is almost physical.', 'addiction_craving', true, true, null, null, 'Go in for one', '{"energy": 12, "cash": -50, "addiction_severity": 12, "addiction_type": "alcohol"}', 'One became several. The bartender knows your order.', 'Keep walking', '{"health": 5, "xp": 12}', 'You walked right past. Proud of yourself.'),
('I Should Get a Drink', 'The thought won''t leave your head. Just one drink...', 'addiction_craving', true, true, null, null, 'Give in', '{"energy": 10, "cash": -25, "addiction_severity": 9, "addiction_type": "alcohol"}', 'The familiar warmth spreads through you.', 'Resist the urge', '{"health": 5, "xp": 14}', 'The craving passes. You feel stronger.'),
('Happy Hour Is Calling', 'Your phone buzzes with a happy hour promo from the local pub.', 'addiction_craving', true, true, null, null, 'Head to happy hour', '{"energy": 15, "cash": -40, "addiction_severity": 11, "addiction_type": "alcohol"}', 'Cheap drinks, expensive consequences.', 'Delete the notification', '{"health": 5, "xp": 12}', 'Out of sight, out of mind. Good call.'),
('Wine Would Be Nice Right Now', 'You spot a bottle of wine in the shop window. It looks perfect.', 'addiction_craving', true, true, null, null, 'Buy a bottle', '{"energy": 8, "cash": -35, "addiction_severity": 9, "addiction_type": "alcohol"}', 'One glass became the whole bottle.', 'Walk away', '{"health": 5, "xp": 11}', 'Not today. You kept walking.'),
('The Minibar Is Right There', 'Alone in a hotel room, the minibar glows temptingly.', 'addiction_craving', true, true, null, null, 'Open the minibar', '{"energy": 10, "cash": -60, "addiction_severity": 13, "addiction_type": "alcohol"}', 'Expensive and regrettable. Double whammy.', 'Ignore it', '{"health": 5, "xp": 16}', 'You turned away. Strong willpower.'),
('I Deserve a Drink After Today', 'What a day. Surely one drink to unwind is fine?', 'addiction_craving', true, true, null, null, 'Treat yourself', '{"energy": 12, "cash": -30, "addiction_severity": 10, "addiction_type": "alcohol"}', 'You deserved about five apparently.', 'Find another way to relax', '{"health": 5, "xp": 13}', 'A walk and some fresh air worked better.'),
('Everyone Else Is Drinking', 'At a social event, everyone has a glass in hand. You feel left out.', 'addiction_craving', true, true, null, null, 'Join in', '{"energy": 10, "cash": -25, "addiction_severity": 8, "addiction_type": "alcohol"}', 'Social pressure won this round.', 'Stick to soft drinks', '{"health": 5, "xp": 15}', 'Nobody even noticed. All in your head.'),
('That Cocktail Menu Looks Amazing', 'A fancy cocktail bar catches your eye with creative drinks.', 'addiction_craving', true, true, null, null, 'Try a cocktail', '{"energy": 10, "cash": -45, "addiction_severity": 10, "addiction_type": "alcohol"}', 'Three cocktails later and your wallet is lighter.', 'Admire from afar', '{"health": 5, "xp": 11}', 'Pretty to look at, better to avoid.'),
('Hair of the Dog', 'You woke up rough. A morning drink might take the edge off.', 'addiction_craving', true, true, null, null, 'Morning drink', '{"energy": 8, "cash": -20, "addiction_severity": 14, "addiction_type": "alcohol"}', 'This is becoming a dangerous pattern.', 'Coffee instead', '{"health": 5, "xp": 18}', 'Coffee is the healthier wake-up call.'),
('Free Drinks at the VIP Lounge', 'Someone offers you free drinks at a VIP event.', 'addiction_craving', true, true, null, null, 'Accept the offer', '{"energy": 15, "cash": 0, "addiction_severity": 12, "addiction_type": "alcohol"}', 'Free booze is the most expensive kind.', 'Politely decline', '{"health": 5, "xp": 14}', 'You said no to free drinks. Respect.'),
('The Pub Quiz Needs Me', 'Your mates are doing a pub quiz. Hard to do that sober...', 'addiction_craving', true, true, null, null, 'Go to the quiz', '{"energy": 10, "cash": -35, "addiction_severity": 9, "addiction_type": "alcohol"}', 'You won the quiz but lost the battle.', 'Skip it tonight', '{"health": 5, "xp": 12}', 'You can quiz from home. No drink needed.'),
('Champagne to Celebrate', 'Good news deserves a toast, right?', 'addiction_craving', true, true, null, null, 'Pop a bottle', '{"energy": 12, "cash": -50, "addiction_severity": 10, "addiction_type": "alcohol"}', 'One toast became a whole bottle situation.', 'Celebrate differently', '{"health": 5, "xp": 13}', 'Celebrated with a nice meal instead.'),
('Rough Day, Need a Nightcap', 'The stress is unbearable. A nightcap would help you sleep.', 'addiction_craving', true, true, null, null, 'Pour a nightcap', '{"energy": 8, "cash": -20, "addiction_severity": 11, "addiction_type": "alcohol"}', 'You slept but the craving grew stronger.', 'Try herbal tea', '{"health": 5, "xp": 10}', 'Chamomile tea did the trick. Well done.'),
('The Brewery Tour Looks Fun', 'A local brewery is offering tours with free tastings.', 'addiction_craving', true, true, null, null, 'Go on the tour', '{"energy": 10, "cash": -25, "addiction_severity": 10, "addiction_type": "alcohol"}', 'Free tastings turned into paid rounds.', 'Not the best idea right now', '{"health": 5, "xp": 12}', 'You know yourself well enough. Smart choice.'),
('Whiskey Would Warm Me Up', 'It is cold outside. A whiskey would warm your bones.', 'addiction_craving', true, true, null, null, 'Order a whiskey', '{"energy": 10, "cash": -30, "addiction_severity": 9, "addiction_type": "alcohol"}', 'Warm inside, but the craving burns hotter.', 'Hot chocolate instead', '{"health": 5, "xp": 11}', 'Hot chocolate hit the spot perfectly.'),
('Pre-Drinks Before the Show', 'There is a gig tonight. Everyone pre-drinks before shows...', 'addiction_craving', true, true, null, null, 'Pre-drink', '{"energy": 12, "cash": -25, "addiction_severity": 10, "addiction_type": "alcohol"}', 'The show was a blur but the hangover will not be.', 'Go to the show sober', '{"health": 5, "xp": 14}', 'You enjoyed the music more than you expected sober.'),
('Liquid Courage', 'Big meeting tomorrow. A drink to calm the nerves?', 'addiction_craving', true, true, null, null, 'Calm the nerves', '{"energy": 8, "cash": -20, "addiction_severity": 8, "addiction_type": "alcohol"}', 'Nerves calmed but judgement impaired.', 'Prepare properly instead', '{"health": 5, "xp": 15}', 'Preparation beats Dutch courage every time.'),
('The After-Show Drinks', 'The band finished playing. Everyone is heading to the bar.', 'addiction_craving', true, true, null, null, 'Join the after-show', '{"energy": 12, "cash": -40, "addiction_severity": 11, "addiction_type": "alcohol"}', 'Great night, terrible morning incoming.', 'Head home', '{"health": 5, "xp": 13}', 'You will thank yourself in the morning.'),
('Flight Delayed, Bar Open', 'Your flight is delayed 3 hours. The airport bar is right there.', 'addiction_craving', true, true, null, null, 'Airport drinks', '{"energy": 10, "cash": -45, "addiction_severity": 10, "addiction_type": "alcohol"}', 'Airport prices hit different.', 'Browse the shops instead', '{"health": 5, "xp": 12}', 'Bought a book instead. Better investment.'),
('Open Bar at the Party', 'You got invited to a party with an open bar.', 'addiction_craving', true, true, null, null, 'Take advantage', '{"energy": 15, "cash": 0, "addiction_severity": 13, "addiction_type": "alcohol"}', 'Open bar, open floodgates.', 'Just socialise', '{"health": 5, "xp": 16}', 'You proved you can party without drinking.'),
('Bottomless Brunch', 'A friend invites you to bottomless brunch.', 'addiction_craving', true, true, null, null, 'Bottomless it is', '{"energy": 12, "cash": -40, "addiction_severity": 12, "addiction_type": "alcohol"}', 'The mimosas were bottomless but your dignity was not.', 'Regular brunch is fine', '{"health": 5, "xp": 14}', 'Food without the booze. Novel concept.'),
('Wine Pairing Dinner', 'A fancy restaurant offers a wine pairing menu.', 'addiction_craving', true, true, null, null, 'Go for the pairing', '{"energy": 10, "cash": -60, "addiction_severity": 10, "addiction_type": "alcohol"}', 'Five courses, five wines, one bad decision.', 'Food only', '{"health": 5, "xp": 13}', 'The food was the star. Wine not needed.'),
('Shots Shots Shots', 'Someone at the bar is buying rounds of shots for everyone.', 'addiction_craving', true, true, null, null, 'Join the shots', '{"energy": 15, "cash": -20, "addiction_severity": 14, "addiction_type": "alcohol"}', 'Three shots became ten. Bad idea.', 'No thanks', '{"health": 5, "xp": 17}', 'Peer pressure: 0, You: 1.'),
('It Is Only a Pint', 'Just one pint cannot hurt, right? It is basically water.', 'addiction_craving', true, true, null, null, 'Just one pint', '{"energy": 8, "cash": -20, "addiction_severity": 8, "addiction_type": "alcohol"}', 'That was definitely not just one pint.', 'Not even one', '{"health": 5, "xp": 12}', 'You know one is never just one.'),
('Post-Match Pints', 'Just watched the game. Everyone is going for post-match pints.', 'addiction_craving', true, true, null, null, 'Join the lads', '{"energy": 12, "cash": -35, "addiction_severity": 10, "addiction_type": "alcohol"}', 'Post-match became post-midnight.', 'Head home after the game', '{"health": 5, "xp": 13}', 'Game was enough entertainment for one day.'),
('The Hotel Minibar Restocked', 'They restocked the minibar. All those little bottles...', 'addiction_craving', true, true, null, null, 'Raid the minibar', '{"energy": 10, "cash": -70, "addiction_severity": 12, "addiction_type": "alcohol"}', 'Tiny bottles, massive bill.', 'Put a towel over it', '{"health": 5, "xp": 14}', 'Out of sight, out of mind. Smart.'),
('One for the Road', 'About to leave the venue. One more for the road?', 'addiction_craving', true, true, null, null, 'One more', '{"energy": 8, "cash": -25, "addiction_severity": 9, "addiction_type": "alcohol"}', 'One for the road became three for the taxi.', 'Just leave', '{"health": 5, "xp": 11}', 'The road does not need a drink. Neither do you.');
INSERT INTO public.random_events (title, description, category, is_common, is_active, health_min, health_max, option_a_text, option_a_effects, option_a_outcome_text, option_b_text, option_b_effects, option_b_outcome_text) VALUES
('I Need a Hit', 'The craving is overwhelming. Every cell in your body screams for it.', 'addiction_craving', true, true, null, null, 'Give in', '{"energy": 15, "cash": -50, "addiction_severity": 12, "addiction_type": "substances"}', 'The rush was instant but the shame follows.', 'Fight through it', '{"health": 5, "xp": 20}', 'The worst cravings pass. You survived this one.'),
('Just One More Time Will Not Hurt', 'You tell yourself you can handle it. Just once more.', 'addiction_craving', true, true, null, null, 'One more time', '{"energy": 12, "cash": -60, "addiction_severity": 14, "addiction_type": "substances"}', 'It is never just one more time.', 'That is what you always say', '{"health": 5, "xp": 18}', 'You caught yourself in the lie. Progress.'),
('I Know a Guy Who Can Sort Me Out', 'Your phone is in your hand. One call and it is done.', 'addiction_craving', true, true, null, null, 'Make the call', '{"energy": 12, "cash": -80, "addiction_severity": 15, "addiction_type": "substances"}', 'He sorted you out alright. Right back to square one.', 'Delete the number', '{"health": 5, "xp": 20}', 'You deleted the contact. Brave move.'),
('The Withdrawal Is Killing Me', 'Your body aches. Everything hurts. You know what would stop it.', 'addiction_craving', true, true, null, null, 'Make it stop', '{"energy": 15, "cash": -50, "addiction_severity": 13, "addiction_type": "substances"}', 'The pain stopped but the cycle continues.', 'Ride it out', '{"health": 5, "xp": 20}', 'The pain is temporary. Recovery is worth it.'),
('Someone Offered Me Something', 'At a party, someone casually offers you a bump.', 'addiction_craving', true, true, null, null, 'Accept', '{"energy": 15, "cash": -20, "addiction_severity": 12, "addiction_type": "substances"}', 'Free but the cost to your recovery is immense.', 'No thanks', '{"health": 5, "xp": 16}', 'You said no in a pressure moment. Huge.'),
('I Found My Old Stash', 'Cleaning out a drawer, you find something you hid months ago.', 'addiction_craving', true, true, null, null, 'Use it', '{"energy": 12, "cash": 0, "addiction_severity": 14, "addiction_type": "substances"}', 'The hiding spot you forgot. Your recovery did not forget.', 'Flush it', '{"health": 5, "xp": 20}', 'Down the drain. Along with temptation.'),
('The Comedown Is Brutal', 'You feel awful. Another hit would level you out...', 'addiction_craving', true, true, null, null, 'Level out', '{"energy": 10, "cash": -60, "addiction_severity": 15, "addiction_type": "substances"}', 'Chasing the dragon never works.', 'Suffer through', '{"health": 5, "xp": 18}', 'It is rough but you are getting through it.'),
('Backstage Temptation', 'Backstage at a show, substances are openly available.', 'addiction_craving', true, true, null, null, 'Partake', '{"energy": 15, "cash": -40, "addiction_severity": 13, "addiction_type": "substances"}', 'Rock and roll lifestyle strikes again.', 'Leave backstage', '{"health": 5, "xp": 17}', 'You removed yourself from the situation. Smart.'),
('The Dealer Texted Me', 'An unsolicited text with a special offer. How convenient.', 'addiction_craving', true, true, null, null, 'Reply', '{"energy": 12, "cash": -70, "addiction_severity": 14, "addiction_type": "substances"}', 'Convenience is the enemy of recovery.', 'Block the number', '{"health": 5, "xp": 19}', 'Blocked and deleted. Good riddance.'),
('Everyone Is Doing It at This Party', 'The party is wild. It seems like everyone is on something.', 'addiction_craving', true, true, null, null, 'Join the party', '{"energy": 15, "cash": -50, "addiction_severity": 12, "addiction_type": "substances"}', 'Peer pressure is a powerful drug itself.', 'Leave the party', '{"health": 5, "xp": 16}', 'You left. The party was not worth your progress.'),
('I Cannot Sleep Without It', 'Insomnia is destroying you. You know what helps you sleep.', 'addiction_craving', true, true, null, null, 'Self-medicate', '{"energy": 10, "cash": -40, "addiction_severity": 11, "addiction_type": "substances"}', 'You slept, but at what cost?', 'Try counting sheep', '{"health": 5, "xp": 14}', 'Awful sleep but clean sleep. Worth it.'),
('The Stress Is Unbearable', 'Work pressure is crushing. You know an instant escape.', 'addiction_craving', true, true, null, null, 'Escape', '{"energy": 12, "cash": -50, "addiction_severity": 13, "addiction_type": "substances"}', 'The stress came back double after.', 'Deal with it properly', '{"health": 5, "xp": 17}', 'Faced the stress head on. Healthier approach.'),
('Studio Session Would Be Better High', 'You convince yourself creativity needs chemical help.', 'addiction_craving', true, true, null, null, 'Enhance creativity', '{"energy": 12, "cash": -60, "addiction_severity": 12, "addiction_type": "substances"}', 'The music sounded great then. Not so much sober.', 'Create sober', '{"health": 5, "xp": 18}', 'Real creativity does not need assistance.'),
('My Old Using Buddy Called', 'An old friend calls. They want to hang out like old times.', 'addiction_craving', true, true, null, null, 'Hang out', '{"energy": 10, "cash": -50, "addiction_severity": 14, "addiction_type": "substances"}', 'Old times means old habits.', 'Rain check', '{"health": 5, "xp": 16}', 'Some friendships need new boundaries.'),
('Just to Take the Edge Off', 'Not to get high, just to feel normal. That is what you tell yourself.', 'addiction_craving', true, true, null, null, 'Take the edge off', '{"energy": 10, "cash": -40, "addiction_severity": 11, "addiction_type": "substances"}', 'The edge is still there. Plus new problems.', 'The edge is part of recovery', '{"health": 5, "xp": 15}', 'Uncomfortable but honest. That is growth.'),
('Touring Is So Boring Without It', 'Long stretches on the road. You used to have something for that.', 'addiction_craving', true, true, null, null, 'Old road habits', '{"energy": 12, "cash": -50, "addiction_severity": 13, "addiction_type": "substances"}', 'Tour life is hard. This made it harder.', 'Find new road entertainment', '{"health": 5, "xp": 15}', 'Podcasts and books. Who knew?'),
('The Anxiety Is Crushing', 'Anxiety spikes hard. You know something that kills it instantly.', 'addiction_craving', true, true, null, null, 'Quick fix', '{"energy": 10, "cash": -40, "addiction_severity": 12, "addiction_type": "substances"}', 'Anxiety gone temporarily, guilt permanent.', 'Breathing exercises', '{"health": 5, "xp": 16}', 'Breathe in, breathe out. It passed.'),
('Green Room Temptation', 'The green room has party favours laid out.', 'addiction_craving', true, true, null, null, 'Help yourself', '{"energy": 15, "cash": 0, "addiction_severity": 13, "addiction_type": "substances"}', 'Hospitality that costs your health.', 'Ignore the table', '{"health": 5, "xp": 17}', 'You performed without chemical assistance.'),
('I Feel So Low', 'Depression hits hard. You know a shortcut to feeling good.', 'addiction_craving', true, true, null, null, 'Shortcut to happiness', '{"energy": 12, "cash": -50, "addiction_severity": 14, "addiction_type": "substances"}', 'The high was brief. The low after was worse.', 'Call a friend instead', '{"health": 5, "xp": 18}', 'Talking helped more than any substance could.'),
('Festival Weekend Pressure', 'At a festival. The temptation is everywhere.', 'addiction_craving', true, true, null, null, 'Festival mode', '{"energy": 15, "cash": -60, "addiction_severity": 13, "addiction_type": "substances"}', 'Festival memories you will not remember.', 'Enjoy the music', '{"health": 5, "xp": 16}', 'Festivals are about music, not substances.'),
('Pain Needs Managing', 'Your body hurts from touring. You know what takes the pain away.', 'addiction_craving', true, true, null, null, 'Self-medicate the pain', '{"energy": 10, "cash": -40, "addiction_severity": 12, "addiction_type": "substances"}', 'The physical pain is gone. Addiction pain is worse.', 'See a real doctor', '{"health": 5, "xp": 17}', 'Professional help is always the right call.'),
('Late Night Weakness', 'It is 3am. Willpower is at its lowest. The craving peaks.', 'addiction_craving', true, true, null, null, 'Give in at 3am', '{"energy": 10, "cash": -50, "addiction_severity": 15, "addiction_type": "substances"}', 'Nothing good happens at 3am.', 'Go to bed', '{"health": 5, "xp": 20}', 'Morning light brings clarity. You made it.'),
('Reward Yourself', 'Big achievement today. You deserve a reward...', 'addiction_craving', true, true, null, null, 'Reward time', '{"energy": 12, "cash": -50, "addiction_severity": 12, "addiction_type": "substances"}', 'Some rewards are punishments in disguise.', 'Celebrate clean', '{"health": 5, "xp": 16}', 'Real rewards do not have comedowns.'),
('I Am Bored Out of My Mind', 'Nothing to do. Boredom was always a trigger for you.', 'addiction_craving', true, true, null, null, 'Chase excitement', '{"energy": 12, "cash": -40, "addiction_severity": 11, "addiction_type": "substances"}', 'Exciting for an hour. Regret for a week.', 'Find a hobby', '{"health": 5, "xp": 14}', 'Picked up a book instead. Progress.');
INSERT INTO public.random_events (title, description, category, is_common, is_active, health_min, health_max, option_a_text, option_a_effects, option_a_outcome_text, option_b_text, option_b_effects, option_b_outcome_text) VALUES
('I Should Stick a Bet On', 'The urge to gamble is strong. You feel lucky today.', 'addiction_craving', true, true, null, null, 'Place a bet', '{"energy": 5, "cash": -50, "addiction_severity": 10, "addiction_type": "gambling"}', 'Lost the bet. Of course you did.', 'Pocket your money', '{"health": 5, "xp": 15}', 'Money saved is money earned.'),
('The Casino Is Calling', 'You drive past the casino. The lights are mesmerising.', 'addiction_craving', true, true, null, null, 'Pull into the casino', '{"energy": 8, "cash": -100, "addiction_severity": 14, "addiction_type": "gambling"}', 'The house always wins. Always.', 'Drive on', '{"health": 5, "xp": 18}', 'Kept driving. The lights faded behind you.'),
('I Feel Lucky Today', 'Something tells you today is your day. The universe is on your side.', 'addiction_craving', true, true, null, null, 'Trust the feeling', '{"energy": 5, "cash": -60, "addiction_severity": 11, "addiction_type": "gambling"}', 'The universe lied. You lost.', 'Luck is not real', '{"health": 5, "xp": 14}', 'You trusted logic over superstition.'),
('One Spin Will Not Hurt', 'The slot machine is right there. Just one spin...', 'addiction_craving', true, true, null, null, 'One spin', '{"energy": 5, "cash": -40, "addiction_severity": 10, "addiction_type": "gambling"}', 'One spin became fifty. The machine ate your money.', 'Walk away', '{"health": 5, "xp": 13}', 'Slot machines are designed to trap you. You escaped.'),
('I Should Go to the Casino', 'The weekend stretches ahead. The casino would fill the time.', 'addiction_craving', true, true, null, null, 'Casino weekend', '{"energy": 8, "cash": -80, "addiction_severity": 13, "addiction_type": "gambling"}', 'Weekend gone, wallet empty.', 'Plan something else', '{"health": 5, "xp": 15}', 'Found better ways to spend the weekend.'),
('The Big Game Is On', 'Huge match tonight. It would be so much better with a bet on.', 'addiction_craving', true, true, null, null, 'Place the bet', '{"energy": 5, "cash": -50, "addiction_severity": 10, "addiction_type": "gambling"}', 'Your team won but your bet lost. Typical.', 'Watch without betting', '{"health": 5, "xp": 12}', 'The game was more enjoyable without the stress.'),
('Online Poker Tournament Starting', 'A big online poker tournament is about to start.', 'addiction_craving', true, true, null, null, 'Buy in', '{"energy": 8, "cash": -75, "addiction_severity": 12, "addiction_type": "gambling"}', 'Busted out in the first hour.', 'Close the app', '{"health": 5, "xp": 16}', 'App closed. Temptation closed with it.'),
('Hot Tip From a Friend', 'A mate says he has a sure thing for the races.', 'addiction_craving', true, true, null, null, 'Back the tip', '{"energy": 5, "cash": -60, "addiction_severity": 11, "addiction_type": "gambling"}', 'Your friend tips are as reliable as weather forecasts.', 'No such thing as a sure thing', '{"health": 5, "xp": 14}', 'There is no such thing as a sure thing in gambling.'),
('The Jackpot Is Huge This Week', 'You saw the jackpot amount. What if you won?', 'addiction_craving', true, true, null, null, 'Chase the jackpot', '{"energy": 5, "cash": -40, "addiction_severity": 10, "addiction_type": "gambling"}', 'Did not win. Statistically, you never will.', 'Save your money', '{"health": 5, "xp": 12}', 'Better odds investing in yourself.'),
('Double or Nothing', 'You are down from last time. One big bet could fix everything.', 'addiction_craving', true, true, null, null, 'Double or nothing', '{"energy": 5, "cash": -100, "addiction_severity": 15, "addiction_type": "gambling"}', 'Nothing. It was nothing.', 'Accept the loss', '{"health": 5, "xp": 20}', 'Chasing losses is a fools errand. You are no fool.'),
('The Roulette Table Awaits', 'Red or black. 50/50. How hard can it be?', 'addiction_craving', true, true, null, null, 'Spin the wheel', '{"energy": 5, "cash": -60, "addiction_severity": 12, "addiction_type": "gambling"}', 'It landed on green. The house edge strikes.', 'Do not spin', '{"health": 5, "xp": 14}', 'The only winning move is not to play.'),
('I Could Win It All Back', 'You are deep in the hole. One more bet could turn it around.', 'addiction_craving', true, true, null, null, 'One more bet', '{"energy": 5, "cash": -80, "addiction_severity": 14, "addiction_type": "gambling"}', 'Deeper in the hole. The spiral continues.', 'Stop the bleeding', '{"health": 5, "xp": 18}', 'You stopped. The bleeding stops now.'),
('Free Bet Offer Online', 'A gambling site offers you a free bet to come back.', 'addiction_craving', true, true, null, null, 'Claim the free bet', '{"energy": 5, "cash": -30, "addiction_severity": 10, "addiction_type": "gambling"}', 'The free bet led to twenty paid ones.', 'Unsubscribe', '{"health": 5, "xp": 15}', 'Unsubscribed from temptation.'),
('The Card Game in the Back Room', 'Someone mentions a private high-stakes card game.', 'addiction_craving', true, true, null, null, 'Join the game', '{"energy": 8, "cash": -90, "addiction_severity": 14, "addiction_type": "gambling"}', 'High stakes, higher losses.', 'Not my scene anymore', '{"health": 5, "xp": 17}', 'You have outgrown that scene. Well done.'),
('Scratch Card at the Counter', 'A scratch card at the shop counter catches your eye.', 'addiction_craving', true, true, null, null, 'Buy a scratch card', '{"energy": 5, "cash": -20, "addiction_severity": 8, "addiction_type": "gambling"}', 'Nothing. Again. The small bets add up.', 'Leave it', '{"health": 5, "xp": 10}', 'Small wins are still wins. Leaving it was one.'),
('The Races Are Today', 'Race day! The atmosphere, the excitement, the betting...', 'addiction_craving', true, true, null, null, 'Go to the races', '{"energy": 8, "cash": -70, "addiction_severity": 12, "addiction_type": "gambling"}', 'Great day out, terrible results slip.', 'Skip the races', '{"health": 5, "xp": 14}', 'Race day can wait. Recovery cannot.'),
('I Have a System', 'You have worked out a system. This time will be different.', 'addiction_craving', true, true, null, null, 'Follow the system', '{"energy": 5, "cash": -60, "addiction_severity": 11, "addiction_type": "gambling"}', 'The system does not work. It never did.', 'Systems are nonsense', '{"health": 5, "xp": 15}', 'The only system that works is not gambling.'),
('VIP Casino Invite', 'The casino sent you a VIP invitation with bonus chips.', 'addiction_craving', true, true, null, null, 'Accept the VIP treatment', '{"energy": 10, "cash": -80, "addiction_severity": 13, "addiction_type": "gambling"}', 'Bonus chips spent, real money followed.', 'Decline the invite', '{"health": 5, "xp": 16}', 'VIP status is not worth your recovery.'),
('Crypto Dip Time to Gamble', 'A crypto dip looks like a buying opportunity. Risky though.', 'addiction_craving', true, true, null, null, 'YOLO invest', '{"energy": 5, "cash": -70, "addiction_severity": 11, "addiction_type": "gambling"}', 'The dip kept dipping. So did your wallet.', 'Not worth the risk', '{"health": 5, "xp": 14}', 'Investing is not gambling. But this was.'),
('The Poker Face Is Ready', 'You catch your reflection. You look like a winner tonight.', 'addiction_craving', true, true, null, null, 'Hit the tables', '{"energy": 8, "cash": -60, "addiction_severity": 12, "addiction_type": "gambling"}', 'Looking like a winner, playing like a loser.', 'Winners quit', '{"health": 5, "xp": 15}', 'The real poker face is saying no.'),
('Betting App Notification', 'Your betting app sends a push notification with boosted odds.', 'addiction_craving', true, true, null, null, 'Check the odds', '{"energy": 5, "cash": -40, "addiction_severity": 10, "addiction_type": "gambling"}', 'Boosted odds, depleted funds.', 'Delete the app', '{"health": 5, "xp": 18}', 'App deleted. Notifications silenced forever.'),
('Fantasy League Gambling', 'Your fantasy league has a cash prize pool. Entry fee is steep.', 'addiction_craving', true, true, null, null, 'Enter the league', '{"energy": 5, "cash": -50, "addiction_severity": 9, "addiction_type": "gambling"}', 'Fantasy league, real losses.', 'Free league only', '{"health": 5, "xp": 12}', 'Fun without the financial risk.'),
('Dice Game in the Alley', 'Some guys are running a dice game. Quick money potential.', 'addiction_craving', true, true, null, null, 'Roll the dice', '{"energy": 8, "cash": -60, "addiction_severity": 12, "addiction_type": "gambling"}', 'Street dice: risky in every sense.', 'Keep walking', '{"health": 5, "xp": 15}', 'Dark alleys and dice do not mix with recovery.'),
('All-In Moment', 'You feel the urge to go all-in on something. Anything.', 'addiction_craving', true, true, null, null, 'Go all-in', '{"energy": 5, "cash": -100, "addiction_severity": 15, "addiction_type": "gambling"}', 'All in, all gone.', 'Fold', '{"health": 5, "xp": 20}', 'Folding is the smartest play at the table.'),
('The Slot Machine Winked at Me', 'Walking past the arcade, a flashy slot machine catches your eye.', 'addiction_craving', true, true, null, null, 'Feed the machine', '{"energy": 5, "cash": -30, "addiction_severity": 9, "addiction_type": "gambling"}', 'The machine took your money and gave nothing back.', 'Machines do not wink', '{"health": 5, "xp": 12}', 'You know better. Machines are designed to take.');
INSERT INTO public.random_events (title, description, category, is_common, is_active, health_min, health_max, option_a_text, option_a_effects, option_a_outcome_text, option_b_text, option_b_effects, option_b_outcome_text) VALUES
('There Is a Party I Cannot Miss', 'Everyone who is anyone will be there tonight.', 'addiction_craving', true, true, null, null, 'Go to the party', '{"energy": -15, "cash": -40, "addiction_severity": 10, "addiction_type": "partying"}', 'Epic night but your body is wrecked.', 'Stay in', '{"health": 5, "xp": 12}', 'FOMO faded by morning. Good choice.'),
('One More Night Out Will Not Hurt', 'You went out last night. And the night before. But tonight...', 'addiction_craving', true, true, null, null, 'One more night', '{"energy": -20, "cash": -50, "addiction_severity": 12, "addiction_type": "partying"}', 'Three nights in a row. Your body is screaming.', 'Rest tonight', '{"health": 5, "xp": 14}', 'Your body needed rest more than another party.'),
('I Should Go Out Tonight', 'The night is young and your bed seems boring.', 'addiction_craving', true, true, null, null, 'Hit the town', '{"energy": -15, "cash": -35, "addiction_severity": 9, "addiction_type": "partying"}', 'Fun night but tomorrow will be rough.', 'Early night', '{"health": 5, "xp": 11}', 'Early night, early morning. Productive.'),
('VIP Table Available', 'A promoter offers you a VIP table at the hottest club.', 'addiction_craving', true, true, null, null, 'VIP all night', '{"energy": -20, "cash": -80, "addiction_severity": 13, "addiction_type": "partying"}', 'VIP treatment, VIP hangover.', 'Turn it down', '{"health": 5, "xp": 16}', 'VIP of self-control. Better title.'),
('The Afterparty Is Just Starting', 'The club closed but the afterparty is just beginning.', 'addiction_craving', true, true, null, null, 'Afterparty time', '{"energy": -20, "cash": -40, "addiction_severity": 14, "addiction_type": "partying"}', '6am. No idea where you are. Classic afterparty.', 'Go home', '{"health": 5, "xp": 18}', 'Home is where the recovery is.'),
('DJ Set at the Underground Club', 'Your favourite DJ is playing an underground set tonight.', 'addiction_craving', true, true, null, null, 'Underground vibes', '{"energy": -15, "cash": -30, "addiction_severity": 10, "addiction_type": "partying"}', 'Great set but you cannot function tomorrow.', 'Listen online', '{"health": 5, "xp": 13}', 'Livestream from the comfort of your sofa.'),
('Festival Afterparty', 'The festival ended but the afterparty is legendary.', 'addiction_craving', true, true, null, null, 'One more event', '{"energy": -20, "cash": -50, "addiction_severity": 12, "addiction_type": "partying"}', 'Your body has been partying for 72 hours straight.', 'Call it a night', '{"health": 5, "xp": 15}', 'Knowing when to stop is a superpower.'),
('House Party Invite', 'Got invited to a house party. No rules, no closing time.', 'addiction_craving', true, true, null, null, 'Party at the house', '{"energy": -15, "cash": -25, "addiction_severity": 11, "addiction_type": "partying"}', 'No closing time meant no sleep.', 'Send a polite decline', '{"health": 5, "xp": 12}', 'Polite decline, peaceful night.'),
('The Club Has No Queue Tonight', 'Usually there is a massive queue but tonight it is empty. A sign?', 'addiction_craving', true, true, null, null, 'It is a sign', '{"energy": -15, "cash": -35, "addiction_severity": 10, "addiction_type": "partying"}', 'No queue does not mean no consequences.', 'Signs are not real', '{"health": 5, "xp": 13}', 'The only sign that matters is the stop sign.'),
('Rooftop Party Downtown', 'A celebrity rooftop party downtown. Exclusive guest list.', 'addiction_craving', true, true, null, null, 'Rooftop vibes', '{"energy": -15, "cash": -60, "addiction_severity": 12, "addiction_type": "partying"}', 'Great views, terrible decisions.', 'Watch the sunset from home', '{"health": 5, "xp": 14}', 'Home sunset. Same sky, less damage.'),
('Pre-Release Party', 'A pre-release party for a big album. Networking opportunity?', 'addiction_craving', true, true, null, null, 'Network at the party', '{"energy": -15, "cash": -40, "addiction_severity": 10, "addiction_type": "partying"}', 'Networking turned into partying real quick.', 'Network during the day', '{"health": 5, "xp": 13}', 'Daytime networking is more productive anyway.'),
('Pool Party Season', 'Summer pool party at a producer mansion.', 'addiction_craving', true, true, null, null, 'Dive in', '{"energy": -15, "cash": -45, "addiction_severity": 11, "addiction_type": "partying"}', 'The pool was fun. The 2am taxi was not.', 'Pass on the pool', '{"health": 5, "xp": 12}', 'Summer does not require pool parties.'),
('Warehouse Rave This Weekend', 'An illegal warehouse rave is happening this weekend.', 'addiction_craving', true, true, null, null, 'Rave all night', '{"energy": -20, "cash": -30, "addiction_severity": 14, "addiction_type": "partying"}', 'Raving until dawn. Your ears are ringing for days.', 'Too risky', '{"health": 5, "xp": 17}', 'Illegal and unhealthy. Double no.'),
('Birthday Party Cannot Be Missed', 'A close friend birthday party tonight. Big one.', 'addiction_craving', true, true, null, null, 'Party for them', '{"energy": -15, "cash": -50, "addiction_severity": 10, "addiction_type": "partying"}', 'Happy birthday to them, unhappy morning for you.', 'Send a gift instead', '{"health": 5, "xp": 12}', 'A thoughtful gift says more than a drunk appearance.'),
('The Night Is Still Young', 'It is only midnight. The party has not even peaked yet.', 'addiction_craving', true, true, null, null, 'Stay till it peaks', '{"energy": -20, "cash": -35, "addiction_severity": 12, "addiction_type": "partying"}', 'It peaked at 4am. So did your regret.', 'Midnight is late enough', '{"health": 5, "xp": 14}', 'Midnight exit is the power move.'),
('Club Opening Night', 'A brand new club is opening tonight. Everyone will be there.', 'addiction_craving', true, true, null, null, 'Be there opening night', '{"energy": -15, "cash": -50, "addiction_severity": 11, "addiction_type": "partying"}', 'Opening night hype, next-day crash.', 'Clubs are not going anywhere', '{"health": 5, "xp": 13}', 'It will still be there next month. When you are ready.'),
('Beach Party at Sunset', 'A beach party with bonfires, music, and no rules.', 'addiction_craving', true, true, null, null, 'Beach party time', '{"energy": -15, "cash": -30, "addiction_severity": 10, "addiction_type": "partying"}', 'Sand in places, regret in others.', 'Beach walk instead', '{"health": 5, "xp": 12}', 'A quiet beach walk is good for the soul.'),
('Backstage Access After the Show', 'You have got backstage access. The party back there is wild.', 'addiction_craving', true, true, null, null, 'Backstage madness', '{"energy": -20, "cash": -40, "addiction_severity": 13, "addiction_type": "partying"}', 'Backstage stories you cannot tell anyone.', 'Watch from the crowd', '{"health": 5, "xp": 15}', 'The show was the main event. Not the afterparty.'),
('Last Night of Tour', 'Last night of tour. Tradition says it is the biggest party.', 'addiction_craving', true, true, null, null, 'Tour finale blowout', '{"energy": -20, "cash": -60, "addiction_severity": 14, "addiction_type": "partying"}', 'Tour ended with a bang. And a splitting headache.', 'Celebrate calmly', '{"health": 5, "xp": 16}', 'Celebrated the achievement, not the destruction.'),
('Just One More Song on the Dance Floor', 'Your legs hurt but the DJ just dropped your favourite track.', 'addiction_craving', true, true, null, null, 'One more song', '{"energy": -10, "cash": -20, "addiction_severity": 8, "addiction_type": "partying"}', 'One more song became one more hour.', 'Leave on a high', '{"health": 5, "xp": 11}', 'Left when the vibe was perfect. Great exit.');
